                result = Functions.Update(credentials, "LK.CUSTOMERS", "2" + ASCII_Chars.FS_chr + "CUSTOMER UPDATE 2þADDRESS 2þ444" + ASCII_Chars.FS_chr + "");
            }
            catch (Exception ex)
            {
                string error = ex.Message;
                // Do something
            }
            return result;
