Imports Linkar
Imports Linkar.Functions.Direct.MV

Class Test

    Public Function MyNew() As String
        Dim result As String = ""

        Try
            Dim credentials As CredentialOptions = New CredentialOptions("127.0.0.1", "EPNAME", 11300, "admin", "admin")


            result = Functions.New(credentials, "LK.CUSTOMERS","2" + ASCII_Chars.FS_chr + "CUSTOMER 2þADDRESS 2þ444")			
		Catch ex As Exception

            Dim[error] As String = ex.Message
			' Do something
		End Try

        Return result
  End Function
End Class

